let header=document.querySelector(".header");function showHeaderElements(){header.classList.remove("header--hidden")}function hideHeaderElements(){header.classList.add("header--hidden")}function openOrCloseAnswer(e){e.target.parentNode.classList.toggle("faq__questions__item--open")}function removeActiveButton(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--active")}function hideAllTabs(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),n=document.querySelectorAll("[data-faq-question]");let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?hideHeaderElements:showHeaderElements)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);hideAllTabs(),t.classList.add("shows__list--active"),removeActiveButton(),e.target.classList.add("shows__tabs__button--active")});for(let e=0;e<n.length;e++)n[e].addEventListener("click",openOrCloseAnswer)});